        nodes

			&datasource-settings;
            planet_osm_line


            (select ST_StartPoint(way) as way from planet_osm_line where "piste:type"='nordic') as nodes






		nodes2

			&datasource-settings;
            planet_osm_line


            (select ST_StartPoint(way) as way from planet_osm_line where "piste:type"='nordic') as nodes





        downhill

			&datasource-settings;

            (select * from planet_osm_line where "piste:type"='downhill'order by z_order) as pistes





        nordic-bg

			&datasource-settings;
			planet_osm_line

			(select * from planet_osm_line where "piste:type"='nordic' and osm_id > 0 order by z_order) as pistes





        nordic

			&datasource-settings;
			planet_osm_line

			(select * from planet_osm_line where "piste:type"='nordic' and osm_id > 0 order by z_order) as pistes





        eraser

			&datasource-settings;
            planet_osm_line

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id < 0 order by z_order) as routes





        relation-bg

			&datasource-settings;
            planet_osm_line

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id < 0 order by z_order) as routes





        relation

			&datasource-settings;
            planet_osm_line

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id < 0 ORDER BY ST_LENGTH(way) ASC) as routes





        difficulty

			&datasource-settings;

            (SELECT * FROM planet_osm_line where "piste:type"='nordic' and osm_id > 0 order by z_order) as pistes





        no-difficulty

			&datasource-settings;

            (SELECT * FROM planet_osm_line where "piste:type"='nordic' and ("piste:difficulty" not in ('novice','easy','intermediate','advanced','expert','freeride','extreme') or "piste:difficulty" is null) and osm_id > 0 order by z_order) as pistes





        difficulty-icons

			&datasource-settings;

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id > 0 order by z_order) as pistes





        text

			&datasource-settings;
			planet_osm_line

            (select * from planet_osm_line where "piste:type"='nordic' and osm_id < 0 order by z_order) as text





        site_text

			&datasource-settings;
			planet_osm_point

            (select * from planet_osm_point where site_name is not null) as text
